function getMid(r,n){return Math.floor((r+n)/2)}function randomNum(r,n){return Math.floor(Math.random()*(n-r+1)+r)}function randomElementFrom(r){return r[Math.floor(Math.random()*r.length)]}function filterArray(r,n){return r.filter(r=>r!=n)}function sumOfArray(r){return r.reduce((r,n)=>r+n,0)}function descending(r){return r.sort((r,n)=>n.localeCompare(r))}function minArray(r){return Math.min(...r)}function maxArray(r){return Math.max(...r)}function reverseString(r){return r.split("").reverse().join("")}function capitalize(r){return r.charAt(0).toUpperCase()+r.slice(1)}function reverseNum(r){if(r<10&&-10<r)return r;var n=+(10<r?r:-1*r).toString().split("").reverse().join("");return 10<r?n:-1*n}function isEven(r){return r%2==0}function isOdd(r){return r%2!=0}function isPalindrome(r){return r.toString()==reverseString(r.toString())}function isPrime(r){for(i=2,s=Math.sqrt(r);i<=s;i++)if(r%i==0)return!1;return!0}