function getMid(n,r){return Math.floor((n+r)/2)}function randomNum(n,r){return Math.floor(Math.random()*(r+1)+n)}function randomElementFrom(n){return n[Math.floor(Math.random()*n.length)]}function filterArray(n,r){return n.filter(n=>n!=r)}function sumOfArray(n){return n.reduce((n,r)=>n+r,0)}function ascending(n){n.sort(function(n,r){return n-r})}function descending(n){n.sort(function(n,r){return n-r}).reverse()}function minArray(n){return Math.min(...n)}function maxArray(n){return Math.max(...n)}function removeDuplicate(n){let t=Array.isArray(n)?n:n.toString().split("");t.filter(function(n,r){return t.indexOf(n)===r})}